import 'package:dartz/dartz.dart';import 'package:tdd_learning/core/error/failures.dart';import 'package:tdd_learning/domain/entities/post_entityes.dart';import 'package:tdd_learning/domain/repositories/post_base_repository.dart';import 'package:tdd_learning/domain/usecases/usecase.dart';class GetAllPostUseCase extends UseCase<List<Post>, NoParams> {  final PostBaseRepository repository;  GetAllPostUseCase({required this.repository});  @override  Future<Either<Failure, List<Post>>> call(NoParams params) async {    return await repository.getAllPost();  }}